import './Table.css'

const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });

const Table = (props)=>{
    return (
        <table className="result">
        <thead>
          <tr>
            <th>Year</th>
            <th>Total Savings</th>
            <th>Interest (Year)</th>
            <th>Total Interest</th>
            <th>Invested Capital</th>
          </tr>
        </thead>
        <tbody>
            {props.data.map(yearData =>{
                return (
                    <tr key={yearData.year}>
                    <td>formatter.format({yearData.year})</td>
                    <td>{yearData.savingsEndOfYear}</td>
                    <td>{yearData.yearlyInterest}</td>
                    <td>{yearData.savingsEndOfYear - props.currentInvestment - yearData.yearlyContribution * yearData.year }</td>
                    <td>{props.currentInvestment+yearData.yearlyContribution * yearData.year}</td>
                  </tr>
                )

            })}

        </tbody>
      </table>
    )

}

export default Table;
