import { useLoaderData, json, defer} from 'react-router-dom';
import EventsList from '../components/EventsList';

function EventsPage() {
   const data = useLoaderData();
  //  if (data.isError){
  //   return data.message;
  //  }
   const events = data.events;

  return (
    <>
      {<EventsList events={events} />}
    </>
  );
}


export default EventsPage;

async function eventLoaders(){
  const response = await fetch('http://localhost:8080/events');

  if (!response.ok) {
    // return {isError: true, message: "Could not fetch the data"}
    // throw new Response (JSON.stringify({message: "Could not fetch the data"}), {
    //   status : 500})
    throw json({message: "Could not fetch the data"}, {status: 500})

  } 
    return response;

}

export async function loader(){
    defer({
      events: eventLoaders()
    })

}



