import React from 'react';

import MoviesList from './components/MoviesList';
import './App.css';
import { useState } from 'react';

function App() {
  const [movies, setMovies] = useState([])
  // const dummyMovies = [
  //   {
  //     id: 1,
  //     title: 'Some Dummy Movie',
  //     openingText: 'This is the opening text of the movie',
  //     releaseDate: '2021-05-18',
  //   },
  //   {
  //     id: 2,
  //     title: 'Some Dummy Movie 2',
  //     openingText: 'This is the second opening text of the movie',
  //     releaseDate: '2021-05-19',
  //   },
  // ];

    function fetchMoviesListHandler(){
    fetch("https://swapi.py4e.com/api/films").then(response => {
      return response.json();
    }).then(responseData => 
     {
      const transformedMovies = responseData.results.map((movie) => {
        return {id : movie.episode_id,
        title : movie.title,
        releaseDate : movie.release_date,
        openingText : movie.opening_crawl
      }
      })
      setMovies(transformedMovies);

     } 
    )

  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesListHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
